// カテゴリ別に問題数を返す
export function getQuestionsByCategory(category) {
  return QUESTIONS.filter(q => q.category === category);
}

// ランダムに問題を返す
export function getRandomQuestions(count = 10, category = null) {
  const pool = category ? getQuestionsByCategory(category) : QUESTIONS;
  const shuffled = [...pool].sort(() => Math.random() - 0.5);
  return shuffled.slice(0, count);
}

// 試験回数の模擬テスト用（全カテゴリから均等抽出）
export function getMockExam(questionsPerCategory = 5) {
  return CATEGORIES.flatMap(cat => {
    const pool = getQuestionsByCategory(cat);
    return [...pool].sort(() => Math.random() - 0.5).slice(0, questionsPerCategory);
  });
}

// シャッフル関数（page.jsから呼ばれる）
export function shuffle(array) {
  const arr = [...array];
  for (let i = arr.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [arr[i], arr[j]] = [arr[j], arr[i]];
  }
  return arr;
}

// 苦手問題を取得（IDリストから）
export function getWeakQuestions(wrongQuestionIds) {
  return QUESTIONS.filter(q => wrongQuestionIds.includes(q.id));
}
